mapper.select:
select

from pwbh_jbxx
where 1=1
 and tsid=#{tsid, jdbcType=VARCHAR}
 and ssqy=#{ssqy, jdbcType=VARCHAR}
 and tssj=#{tssj, jdbcType=TIMESTAMP}
 and azdd_ddh=#{azddDdh, jdbcType=VARCHAR}

mapper.update:
update pwbh_jbxx set tsid=#{tsid}
, ssqy=#{ssqy, jdbcType=VARCHAR}
, tssj = str_to_date(#{tssj},'%Y-%m-%d')
, azdd_ddh=#{azddDdh, jdbcType=VARCHAR}
, ssxl=#{ssxl, jdbcType=VARCHAR}
, lm=#{lm, jdbcType=VARCHAR}
, gzfzr=#{gzfzr, jdbcType=VARCHAR}
, gzry=#{gzry, jdbcType=VARCHAR}
, czr=#{czr, jdbcType=VARCHAR}
, czsj=#{czsj, jdbcType=TIMESTAMP}
, beizhu=#{beizhu, jdbcType=VARCHAR}
, shr=#{shr, jdbcType=VARCHAR}
where  tsid=#{tsid}

mapper.select:
select j.TSID, j.SSQY, j.TSSJ, j.AZDD_DDH, j.SSXL, j.LM from pwbh_jbxx j left join sys_ssxl s on j.ssxl = s.id where 1=1 and j.SSQY=#{ssqy}
and j.TSSJ >= str_to_date(#{startTssj},'%Y-%m-%d')
and j.TSSJ <= str_to_date(#{endTssj},'%Y-%m-%d')
and j.AZDD_DDH like '%${azddDdh}%' /*挖坑操作*/ and j.ssxl = #{xlmc} and s.bdz = #{bdz}
order by TSSJ desc
